import React from 'react';
import { Grommet, Box, Main, Heading, Text, Meter, Grid } from 'grommet';
import { Entete } from './features/Header/Header';
import { Welcome } from './features/Body/Welcome';
import { Carte } from './features/Body/Card';
import { Map } from './features/Body/Map';
import data from './data.json';
import Vid from './features/Body/Video';
import Stats from './features/Body/Stats';

const theme = {
  global: {
    font: {
      family: 'Roboto',
      size: '18px',
      height: '20px',
    },
  },
};

export const App = () => {
  return( 
    <Grommet theme={theme} full>
      <Entete />
      <Welcome />
      <Main background='{neutral-1}' justify='between'>
        <Box margin='xlarge' direction='row'  justify='around'>
        {
          data.map((info) => {
            return (
              <Carte key={info.name} name={info.name} description={info.description} id={info.id} footer={info.footer} />
            )
          })
        }
        </Box>
      </Main>
      <Box id='golf' background='dark-2' pad='large' align='center'>
        <Heading alignSelf='center'>Suivez mon parcours de Golf </Heading>
        <Text alignSelf='center'>En effet vous pourrez me suivre sur cette Map lors de mes diff√©rents tournois du circuit mondial !</Text>
        <Map/>
      </Box>
      <Box height='large'>
          <Vid/>
      </Box>
      <Grid rows={['xxsmall', 'xsmall']} columns={['xsmall', 'small']} gap="small" areas={[{ name: 'header', start: [0, 0], end: [1, 0] },{ name: 'nav', start: [0, 1], end: [0, 1] },{ name: 'main', start: [1, 1], end: [1, 1] },]}>
        <Box gridArea="header" background="brand" />
        <Box gridArea="nav" background="light-5" />
        <Box gridArea="main" background="light-2" />
      </Grid>
      <Box height='large'>
          <Stats/>
          <Meter type="circle" background="light-2" values={[6,30]} />
      </Box>
      
    </Grommet>
    )
}

export default App
