import React from 'react';
import { Grommet, Box, Text } from 'grommet';

const theme = {
  global: {
    font: {
      family: 'Roboto',
      size: '18px',
      height: '20px',
    },
  },
};

function App() {
  return( 
  <Grommet theme={theme} full>
    <Box justify='center'>
      <Text justify='center'>Hello Igor</Text>
    </Box>
  </Grommet>
  )
}

export default App;
